import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
} from 'react-native';
import ClientesList from '../components/ClientesList';
import EstatisticasCard from '../components/EstatisticasCard';
import VendasChart from '../components/VendasChart';
import AddClienteModal from '../components/AddClienteModal';
import VendasModal from '../components/VendasModal';
import AddVendaModal from '../components/AddVendaModal';
import { api } from '../services/api';

interface DashboardScreenProps {
  onLogout: () => void;
}

interface Cliente {
  id: number;
  nomeCompleto: string;
  email: string;
  dataNascimento: string;
  vendas: Array<{ 
    data: string;
    valor: number;
  }>;
}

interface Estatisticas {
  maiorVolume: {
    cliente: string;
    total: number;
  };
  maiorMedia: {
    cliente: string;
    media: number;
  };
  maiorFrequencia: {
    cliente: string;
    dias: number;
  };
}

export default function DashboardScreen({ onLogout }: DashboardScreenProps) {
  const [clientes, setClientes] = useState<Cliente[]>([]);
  const [estatisticas, setEstatisticas] = useState<Estatisticas | null>(null);
  const [vendasPorDia, setVendasPorDia] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showVendasModal, setShowVendasModal] = useState(false);
  const [showAddVendaModal, setShowAddVendaModal] = useState(false);
  const [selectedCliente, setSelectedCliente] = useState<Cliente | null>(null);

  // Estados de loading individuais
  const [isAddingCliente, setIsAddingCliente] = useState(false);
  const [isAddingVenda, setIsAddingVenda] = useState(false);
  const [isUpdatingCliente, setIsUpdatingCliente] = useState(false);
  const [isDeletingCliente, setIsDeletingCliente] = useState(false);
  const [isLoadingClientes, setIsLoadingClientes] = useState(false);
  const [isLoadingEstatisticas, setIsLoadingEstatisticas] = useState(false);
  const [isLoadingVendas, setIsLoadingVendas] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setIsLoading(true);
      await Promise.all([
        loadClientes(),
        loadEstatisticas(),
        loadVendasPorDia(),
      ]);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loadClientes = async () => {
    try {
      setIsLoadingClientes(true);
      console.log('Carregando clientes...');
      const response = await api.get('/clientes');
      console.log('Resposta da API:', response.data);
      const normalizedClientes = normalizeClientesData(response.data);
      console.log('Clientes normalizados:', normalizedClientes);
      setClientes(normalizedClientes);
    } catch (error) {
      console.error('Error loading clientes:', error);
    } finally {
      setIsLoadingClientes(false);
    }
  };

  const loadEstatisticas = async () => {
    try {
      setIsLoadingEstatisticas(true);
      const response = await api.get('/vendas/estatisticas');
      setEstatisticas(response.data);
    } catch (error) {
      console.error('Error loading estatisticas:', error);
    } finally {
      setIsLoadingEstatisticas(false);
    }
  };

  const loadVendasPorDia = async () => {
    try {
      setIsLoadingVendas(true);
      const response = await api.get('/vendas/por-dia');
      // Ordenar em ordem cronolÃ³gica (mais antiga para mais recente)
      const vendasOrdenadas = response.data.sort((a: any, b: any) => 
        new Date(a.data).getTime() - new Date(b.data).getTime()
      );
      setVendasPorDia(vendasOrdenadas);
    } catch (error) {
      console.error('Error loading vendas por dia:', error);
    } finally {
      setIsLoadingVendas(false);
    }
  };

  const normalizeClientesData = (data: any): Cliente[] => {
    // O backend retorna um array simples de clientes
    if (Array.isArray(data)) {
      return data.map((cliente: any) => ({
        id: cliente.id,
        nomeCompleto: cliente.nomeCompleto,
        email: cliente.email,
        dataNascimento: cliente.dataNascimento,
        vendas: cliente.vendas || [],
      }));
    }
    
    // Fallback para estrutura aninhada (caso exista)
    if (data.data?.clientes) {
      return data.data.clientes.map((cliente: any) => ({
        id: cliente.info?.id || Math.random(),
        nomeCompleto: cliente.info?.nomeCompleto || '',
        email: cliente.info?.detalhes?.email || '',
        dataNascimento: cliente.info?.detalhes?.nascimento || '',
        vendas: cliente.estatisticas?.vendas || [],
      }));
    }
    
    return [];
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadData();
    setRefreshing(false);
  };

  const handleAddCliente = async (novoCliente: any) => {
    try {
      setIsAddingCliente(true);
      console.log('Adicionando cliente:', novoCliente);
      const response = await api.post('/clientes', novoCliente);
      console.log('Cliente adicionado com sucesso:', response.data);
      await loadData();
      setShowAddModal(false);
    } catch (error) {
      console.error('Error adding cliente:', error);
    } finally {
      setIsAddingCliente(false);
    }
  };

  const handleViewVendas = (cliente: Cliente) => {
    setSelectedCliente(cliente);
    setShowVendasModal(true);
  };

  const handleAddVenda = () => {
    setShowAddVendaModal(true);
  };

  const getFirstMissingLetter = (nome: string): string => {
    const alphabet = 'abcdefghijklmnopqrstuvwxyz';
    const nomeLower = nome.toLowerCase();
    
    for (let letter of alphabet) {
      if (!nomeLower.includes(letter)) {
        return letter.toUpperCase();
      }
    }
    return '-';
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Loja de Brinquedos</Text>
        <TouchableOpacity style={styles.logoutButton} onPress={onLogout}>
          <Text style={styles.logoutText}>Sair</Text>
        </TouchableOpacity>
      </View>

      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        <View style={styles.content}>
          <TouchableOpacity
            style={styles.addButton}
            onPress={() => setShowAddModal(true)}
          >
            <Text style={styles.addButtonText}>+ Adicionar Cliente</Text>
          </TouchableOpacity>

          <EstatisticasCard 
            estatisticas={estatisticas} 
            isLoading={isLoading}
          />

          <VendasChart 
            vendasPorDia={vendasPorDia} 
            isLoading={isLoading}
          />

          <ClientesList
            clientes={clientes}
            getFirstMissingLetter={getFirstMissingLetter}
            onRefresh={loadClientes}
            onViewVendas={handleViewVendas}
            isLoading={isLoading}
          />
        </View>
      </ScrollView>

      <AddClienteModal
        visible={showAddModal}
        onClose={() => setShowAddModal(false)}
        onAdd={handleAddCliente}
      />

      {selectedCliente && (
        <VendasModal
          visible={showVendasModal}
          onClose={() => setShowVendasModal(false)}
          clienteId={selectedCliente.id}
          clienteNome={selectedCliente.nomeCompleto}
          onAddVenda={handleAddVenda}
        />
      )}

      {selectedCliente && (
        <AddVendaModal
          visible={showAddVendaModal}
          clienteId={selectedCliente.id}
          clienteNome={selectedCliente.nomeCompleto}
          onSuccess={() => {
            setShowAddVendaModal(false);
            loadData();
          }}
        />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingTop: 50,
    paddingBottom: 20,
    backgroundColor: '#fff',
    borderBottomWidth: 1,
    borderBottomColor: '#e1e8ed',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#2c3e50',
  },
  logoutButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    backgroundColor: '#e74c3c',
    borderRadius: 8,
  },
  logoutText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: '600',
  },
  scrollView: {
    flex: 1,
  },
  content: {
    padding: 20,
  },
  addButton: {
    backgroundColor: '#27ae60',
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 20,
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
});
