import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Alert,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import { api } from '../services/api';
import CustomModal from './CustomModal';

interface AddVendaModalProps {
  visible: boolean;
  clienteId: number;
  clienteNome: string;
  onSuccess: () => void;
}

export default function AddVendaModal({
  visible,
  clienteId,
  clienteNome,
  onSuccess,
}: AddVendaModalProps) {
  const [form, setForm] = useState({
    valor: '',
    data: '',
  });

  const handleSubmit = async () => {
    if (!form.valor || !form.data) {
      Alert.alert('Erro', 'Por favor, preencha todos os campos');
      return;
    }

    const valor = parseFloat(form.valor);
    if (isNaN(valor) || valor <= 0) {
      Alert.alert('Erro', 'Por favor, insira um valor v치lido');
      return;
    }

    const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
    if (!dateRegex.test(form.data)) {
      Alert.alert('Erro', 'Por favor, insira uma data v치lida (YYYY-MM-DD)');
      return;
    }

    try {
      await api.post('/vendas', {
        clienteId,
        valor,
        data: form.data,
      });
      
      setForm({ valor: '', data: '' });
      onSuccess();
      Alert.alert('Sucesso', 'Venda adicionada com sucesso!');
    } catch (error) {
      console.error('Erro ao adicionar venda:', error);
      Alert.alert('Erro', 'Erro ao adicionar venda');
    }
  };

  const handleCancel = () => {
    setForm({ valor: '', data: '' });
  };

  return (
    <CustomModal visible={visible}>
      <View style={styles.modalContent}>
        <Text style={styles.modalTitle}>Adicionar Venda</Text>
        <Text style={styles.clienteInfo}>Cliente: {clienteNome}</Text>

        <TextInput
          style={styles.input}
          placeholder="Valor (ex: 150.50)"
          value={form.valor}
          onChangeText={(text) => setForm({ ...form, valor: text })}
          keyboardType="numeric"
        />

        <TextInput
          style={styles.input}
          placeholder="Data (YYYY-MM-DD)"
          value={form.data}
          onChangeText={(text) => setForm({ ...form, data: text })}
        />

        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={[styles.button, styles.cancelButton]}
            onPress={handleCancel}
          >
            <Text style={styles.cancelButtonText}>Cancelar</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={[styles.button, styles.addButton]}
            onPress={handleSubmit}
          >
            <Text style={styles.addButtonText}>Adicionar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </CustomModal>
  );
}

const styles = StyleSheet.create({
  modalContent: {
    padding: 24,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: 8,
  },
  clienteInfo: {
    fontSize: 16,
    color: '#7f8c8d',
    textAlign: 'center',
    marginBottom: 24,
  },
  input: {
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
    padding: 16,
    marginBottom: 16,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#e1e8ed',
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    gap: 12,
    marginTop: 8,
  },
  button: {
    flex: 1,
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
  },
  cancelButton: {
    backgroundColor: '#95a5a6',
  },
  addButton: {
    backgroundColor: '#27ae60',
  },
  cancelButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
});
