import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
} from 'react-native';
import ValidatedInput from './ValidatedInput';
import DatePicker from './DatePicker';
import AlertModal from './AlertModal';
import { validateName, validateEmail, validateDate, formatDateForBackend } from '../utils/validation';
import { api } from '../services/api';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface Cliente {
  id: number;
  nomeCompleto: string;
  email: string;
  dataNascimento: string;
  vendas: Array<{
    data: string;
    valor: number;
  }>;
}

interface ClientesListProps {
  clientes: Cliente[];
  getFirstMissingLetter: (nome: string) => string;
  onRefresh: () => void;
  onViewVendas: (cliente: Cliente) => void;
}

export default function ClientesList({
  clientes,
  getFirstMissingLetter,
  onRefresh,
  onViewVendas,
}: ClientesListProps) {
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editForm, setEditForm] = useState({
    nomeCompleto: '',
    email: '',
    dataNascimento: '',
  });

  const [editErrors, setEditErrors] = useState({
    nomeCompleto: '',
    email: '',
    dataNascimento: '',
  });

  // Estados para o modal
  const [modalVisible, setModalVisible] = useState(false);
  const [modalConfig, setModalConfig] = useState({
    title: '',
    message: '',
    type: 'info' as 'info' | 'success' | 'error' | 'warning',
    onConfirm: () => {},
    confirmText: 'OK',
    cancelText: 'Cancelar',
    showCancel: false,
  });

  const validateEditForm = (): boolean => {
    const newErrors = {
      nomeCompleto: validateName(editForm.nomeCompleto) || '',
      email: validateEmail(editForm.email) || '',
      dataNascimento: validateDate(editForm.dataNascimento) || '',
    };

    setEditErrors(newErrors);

    return !Object.values(newErrors).some(error => error !== '');
  };

  const handleEdit = (cliente: Cliente) => {
    setEditingId(cliente.id);
    setEditForm({
      nomeCompleto: cliente.nomeCompleto,
      email: cliente.email,
      dataNascimento: cliente.dataNascimento,
    });
    setEditErrors({
      nomeCompleto: '',
      email: '',
      dataNascimento: '',
    });
  };

  const showModal = (config: typeof modalConfig) => {
    setModalConfig(config);
    setModalVisible(true);
  };

  const hideModal = () => {
    setModalVisible(false);
  };

  const handleSave = async () => {
    if (!editingId) return;

    if (!validateEditForm()) {
      showModal({
        title: 'Erro',
        message: 'Por favor, corrija os erros no formulário',
        type: 'error',
        onConfirm: hideModal,
        confirmText: 'OK',
        cancelText: 'Cancelar',
        showCancel: false,
      });
      return;
    }

    try {
      const clienteData = {
        ...editForm,
        dataNascimento: formatDateForBackend(editForm.dataNascimento),
      };

      await api.patch(`/clientes/${editingId}`, clienteData);
      setEditingId(null);
      onRefresh();
      showModal({
        title: 'Sucesso',
        message: 'Cliente atualizado com sucesso!',
        type: 'success',
        onConfirm: hideModal,
        confirmText: 'OK',
        cancelText: 'Cancelar',
        showCancel: false,
      });
    } catch (error) {
      showModal({
        title: 'Erro',
        message: 'Erro ao atualizar cliente',
        type: 'error',
        onConfirm: hideModal,
        confirmText: 'OK',
        cancelText: 'Cancelar',
        showCancel: false,
      });
    }
  };

  const handleDelete = (id: number) => {
    console.log('=== INÍCIO DO HANDLE DELETE ===');
    console.log('Tentando excluir cliente com ID:', id);
    console.log('Tipo do ID:', typeof id);

    showModal({
      title: 'Confirmar exclusão',
      message: 'Tem certeza que deseja excluir este cliente?',
      type: 'warning',
      onConfirm: async () => {
        console.log('=== USUÁRIO CONFIRMOU EXCLUSÃO ===');
        try {
          console.log('Iniciando exclusão...');

          // Teste simples primeiro
          console.log('Fazendo teste GET...');
          const testResponse = await api.get('/clientes');
          console.log('Teste GET funcionou:', testResponse.data.length, 'clientes');

          // Teste POST simples
          console.log('Fazendo teste POST...');
          try {
            const postTest = await api.post('/clientes', {
              nomeCompleto: 'Teste DELETE',
              email: 'teste@delete.com',
              dataNascimento: '2000-01-01'
            });
            console.log('Teste POST funcionou:', postTest.data.id);

            // Agora testa DELETE
            console.log('Fazendo teste DELETE...');
            const deleteTest = await api.delete(`/clientes/test/${postTest.data.id}`);
            console.log('Teste DELETE funcionou:', deleteTest);
          } catch (postError: any) {
            console.log('Teste POST falhou:', postError.message);
          }

          const token = await AsyncStorage.getItem('authToken');
          console.log('Token disponível:', !!token);

          // Teste com axios para debug
          console.log('Fazendo requisição DELETE...');
          console.log('URL:', `/clientes/test/${id}`);

          // Teste com fetch primeiro
          console.log('Testando com fetch...');
          const fetchResponse = await fetch(`http://192.168.0.12:3001/clientes/test/${id}`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
            },
          });
          console.log('Fetch response status:', fetchResponse.status);
          console.log('Fetch response ok:', fetchResponse.ok);

          const response = await api.delete(`/clientes/test/${id}`);
          console.log('Resposta da exclusão:', response);

          hideModal();
          showModal({
            title: 'Sucesso',
            message: 'Cliente excluído com sucesso!',
            type: 'success',
            onConfirm: () => {
              hideModal();
              onRefresh();
            },
            confirmText: 'OK',
            cancelText: 'Cancelar',
            showCancel: false,
          });
        } catch (error: any) {
          console.error('=== ERRO NA EXCLUSÃO ===');
          console.error('Erro ao excluir cliente:', error);
          console.error('Status do erro:', error.response?.status);
          console.error('Detalhes do erro:', error.response?.data || error.message);

          hideModal();
          showModal({
            title: 'Erro',
            message: `Erro ao excluir cliente: ${error.message || error.response?.data?.message || 'Erro desconhecido'}`,
            type: 'error',
            onConfirm: hideModal,
            confirmText: 'OK',
            cancelText: 'Cancelar',
            showCancel: false,
          });
        }
      },
      confirmText: 'Excluir',
      cancelText: 'Cancelar',
      showCancel: true,
    });
  };

  const handleViewVendas = (cliente: Cliente) => {
    onViewVendas(cliente);
  };

  const renderCliente = ({ item }: { item: Cliente }) => {
    const isEditing = editingId === item.id;
    const missingLetter = getFirstMissingLetter(item.nomeCompleto);

    return (
      <View style={styles.clienteCard}>
        <View style={styles.clienteHeader}>
          <View style={styles.clienteInfo}>
            {isEditing ? (
              <View style={styles.editRow}>
                <View style={styles.inputContainer}>
                  <ValidatedInput
                    placeholder="Nome completo"
                    value={editForm.nomeCompleto}
                    onChangeText={(text) => {
                      setEditForm({ ...editForm, nomeCompleto: text });
                      if (editErrors.nomeCompleto) {
                        setEditErrors({ ...editErrors, nomeCompleto: validateName(text) || '' });
                      }
                    }}
                    error={editErrors.nomeCompleto}
                    autoCapitalize="words"
                  />
                </View>
                <View style={styles.editButtons}>
                  <TouchableOpacity
                    style={[styles.actionButton, styles.saveButton]}
                    onPress={handleSave}
                  >
                    <Text style={styles.actionButtonText}>Salvar</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={[styles.actionButton, styles.cancelButton]}
                    onPress={() => setEditingId(null)}
                  >
                    <Text style={styles.actionButtonText}>Cancelar</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ) : (
              <Text style={styles.clienteNome}>{item.nomeCompleto}</Text>
            )}
            <View style={styles.missingLetterContainer}>
              <Text style={styles.missingLetterLabel}>Letra faltante:</Text>
              <Text style={styles.missingLetter}>{missingLetter}</Text>
            </View>
          </View>
          <View style={styles.actions}>
            {!isEditing && (
              <>
                <TouchableOpacity
                  style={[styles.actionButton, styles.viewButton]}
                  onPress={() => handleViewVendas(item)}
                >
                  <Text style={styles.actionButtonText}>Vendas</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.actionButton, styles.editButton]}
                  onPress={() => handleEdit(item)}
                >
                  <Text style={styles.actionButtonText}>Editar</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.actionButton, styles.deleteButton]}
                  onPress={() => {
                    console.log('=== BOTÃO EXCLUIR CLICADO ===');
                    console.log('ID do item:', item.id);
                    handleDelete(item.id);
                  }}
                >
                  <Text style={styles.actionButtonText}>Excluir</Text>
                </TouchableOpacity>
              </>
            )}
          </View>
        </View>

        <View style={styles.clienteDetails}>
          {isEditing ? (
            <>
              <ValidatedInput
                placeholder="Email"
                value={editForm.email}
                onChangeText={(text) => {
                  setEditForm({ ...editForm, email: text });
                  if (editErrors.email) {
                    setEditErrors({ ...editErrors, email: validateEmail(text) || '' });
                  }
                }}
                error={editErrors.email}
                keyboardType="email-address"
                autoCapitalize="none"
              />
              <View style={styles.dateContainer}>
                <Text style={styles.label}>Data de Nascimento</Text>
                <DatePicker
                  value={editForm.dataNascimento}
                  onChange={(date) => {
                    setEditForm({ ...editForm, dataNascimento: date });
                    if (editErrors.dataNascimento) {
                      setEditErrors({ ...editErrors, dataNascimento: validateDate(date) || '' });
                    }
                  }}
                  error={editErrors.dataNascimento}
                />
              </View>
            </>
          ) : (
            <>
              <Text style={styles.clienteDetail}>
                <Text style={styles.detailLabel}>Email:</Text> {item.email}
              </Text>
              <Text style={styles.clienteDetail}>
                <Text style={styles.detailLabel}>Nascimento:</Text>{' '}
                {new Date(item.dataNascimento).toLocaleDateString('pt-BR')}
              </Text>
              <Text style={styles.clienteDetail}>
                <Text style={styles.detailLabel}>Total de vendas:</Text>{' '}
                {item.vendas.length}
              </Text>
            </>
          )}
        </View>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Clientes</Text>
      <FlatList
        data={clientes}
        renderItem={renderCliente}
        keyExtractor={(item) => item.id.toString()}
        showsVerticalScrollIndicator={false}
      />

      <AlertModal
        visible={modalVisible}
        title={modalConfig.title}
        message={modalConfig.message}
        type={modalConfig.type}
        onConfirm={modalConfig.onConfirm}
        onCancel={modalConfig.showCancel ? hideModal : undefined}
        confirmText={modalConfig.confirmText}
        cancelText={modalConfig.cancelText}
      />
    </View>
  );
}

