import React, { useState } from 'react';
import {
  View,
  TouchableOpacity,
  StyleSheet,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import RobotoText from '../components/RobotoText';
import RobotoTextInput from '../components/RobotoTextInput';
import AlertModal from '../components/AlertModal';
import axios from 'axios';

interface LoginScreenProps {
  onLogin: (token: string) => void;
}

export default function LoginScreen({ onLogin }: LoginScreenProps) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Estados para os modais
  const [errorModalVisible, setErrorModalVisible] = useState(false);
  const [credentialsModalVisible, setCredentialsModalVisible] = useState(false);

  const handleLogin = async () => {
    if (!username || !password) {
      setCredentialsModalVisible(true);
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:3001/auth/login', {
        username,
        password,
      });

      if (response.data.access_token) {
        onLogin(response.data.access_token);
      }
    } catch (error) {
      setErrorModalVisible(true);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <KeyboardAvoidingView
        style={styles.container}
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      >
        <View style={styles.content}>
          <RobotoText variant="bold" style={styles.title}>
            Loja de Brinquedos
          </RobotoText>
          <RobotoText variant="regular" style={styles.subtitle}>
            Faça login para continuar
          </RobotoText>

          <View style={styles.form}>
            <RobotoTextInput
              style={styles.input}
              placeholder="Usuário"
              value={username}
              onChangeText={setUsername}
              autoCapitalize="none"
            />

            <RobotoTextInput
              style={styles.input}
              placeholder="Senha"
              value={password}
              onChangeText={setPassword}
              secureTextEntry
            />

            <TouchableOpacity
              style={[styles.button, isLoading && styles.buttonDisabled]}
              onPress={handleLogin}
              disabled={isLoading}
            >
              <RobotoText variant="medium" style={styles.buttonText}>
                {isLoading ? 'Entrando...' : 'Entrar'}
              </RobotoText>
            </TouchableOpacity>
          </View>

          <View style={styles.credentials}>
            <RobotoText variant="regular" style={styles.credentialsText}>
              Credenciais de teste:
            </RobotoText>
            <RobotoText variant="regular" style={styles.credentialsText}>
              Usuário: admin
            </RobotoText>
            <RobotoText variant="regular" style={styles.credentialsText}>
              Senha: password
            </RobotoText>
          </View>
        </View>
      </KeyboardAvoidingView>

      <AlertModal
        visible={errorModalVisible}
        title="Erro"
        message="Credenciais inválidas"
        type="error"
        onConfirm={() => setErrorModalVisible(false)}
        confirmText="OK"
      />

      <AlertModal
        visible={credentialsModalVisible}
        title="Erro"
        message="Por favor, preencha todos os campos"
        type="error"
        onConfirm={() => setCredentialsModalVisible(false)}
        confirmText="OK"
      />
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 28,
    textAlign: 'center',
    color: '#2c3e50',
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    textAlign: 'center',
    color: '#7f8c8d',
    marginBottom: 40,
  },
  form: {
    marginBottom: 40,
  },
  input: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 16,
    marginBottom: 16,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#e1e8ed',
  },
  button: {
    backgroundColor: '#3498db',
    borderRadius: 12,
    padding: 16,
    alignItems: 'center',
  },
  buttonDisabled: {
    backgroundColor: '#bdc3c7',
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
  },
  credentials: {
    backgroundColor: '#ecf0f1',
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
  },
  credentialsText: {
    fontSize: 14,
    color: '#7f8c8d',
    marginBottom: 4,
  },
});
