import React from 'react';
import { View, StyleSheet } from 'react-native';
import Shimmer from './Shimmer';

interface ShimmerCardProps {
  title?: boolean;
  lines?: number;
  style?: any;
}

export default function ShimmerCard({ 
  title = true, 
  lines = 3, 
  style 
}: ShimmerCardProps) {
  return (
    <View style={[styles.container, style]}>
      {title && (
        <Shimmer 
          width="60%" 
          height={20} 
          borderRadius={4} 
          style={styles.title}
        />
      )}
      
      {Array.from({ length: lines }).map((_, index) => (
        <Shimmer
          key={index}
          width="100%"
          height={16}
          borderRadius={4}
          style={styles.line}
        />
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 20,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  title: {
    marginBottom: 16,
  },
  line: {
    marginBottom: 8,
  },
});
