import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Alert,
} from 'react-native';
import ValidatedInput from './ValidatedInput';
import DatePicker from './DatePicker';
import { validateName, validateEmail, validateDate, formatDateForBackend } from '../utils/validation';

interface AddClienteModalProps {
  visible: boolean;
  onClose: () => void;
  onAdd: (cliente: any) => void;
}

export default function AddClienteModal({ visible, onClose, onAdd }: AddClienteModalProps) {
  const [form, setForm] = useState({
    nomeCompleto: '',
    email: '',
    dataNascimento: '',
  });

  const [errors, setErrors] = useState({
    nomeCompleto: '',
    email: '',
    dataNascimento: '',
  });

  const validateForm = (): boolean => {
    const newErrors = {
      nomeCompleto: validateName(form.nomeCompleto) || '',
      email: validateEmail(form.email) || '',
      dataNascimento: validateDate(form.dataNascimento) || '',
    };

    setErrors(newErrors);

    return !Object.values(newErrors).some(error => error !== '');
  };

  const handleSubmit = () => {
    if (!validateForm()) {
      Alert.alert('Erro', 'Por favor, corrija os erros no formulÃ¡rio');
      return;
    }

    const clienteData = {
      ...form,
      dataNascimento: formatDateForBackend(form.dataNascimento),
    };

    onAdd(clienteData);
    handleClose();
  };

  const handleClose = () => {
    setForm({
      nomeCompleto: '',
      email: '',
      dataNascimento: '',
    });
    setErrors({
      nomeCompleto: '',
      email: '',
      dataNascimento: '',
    });
    onClose();
  };

  if (!visible) return null;

  return (
    <View style={styles.overlay}>
      <View style={styles.modal}>
        <View style={styles.header}>
          <Text style={styles.title}>Adicionar Cliente</Text>
          <TouchableOpacity style={styles.closeButton} onPress={handleClose}>
            <Text style={styles.closeButtonText}>â</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.content}>
          <ValidatedInput
            label="Nome Completo"
            placeholder="Digite o nome completo"
            value={form.nomeCompleto}
            onChangeText={(text) => {
              setForm({ ...form, nomeCompleto: text });
              if (errors.nomeCompleto) {
                setErrors({ ...errors, nomeCompleto: validateName(text) || '' });
              }
            }}
            error={errors.nomeCompleto}
            autoCapitalize="words"
          />

          <ValidatedInput
            label="Email"
            placeholder="Digite o email"
            value={form.email}
            onChangeText={(text) => {
              setForm({ ...form, email: text });
              if (errors.email) {
                setErrors({ ...errors, email: validateEmail(text) || '' });
              }
            }}
            error={errors.email}
            keyboardType="email-address"
            autoCapitalize="none"
          />

          <View style={styles.dateContainer}>
            <Text style={styles.label}>Data de Nascimento</Text>
            <DatePicker
              value={form.dataNascimento}
              onChange={(date) => {
                setForm({ ...form, dataNascimento: date });
                if (errors.dataNascimento) {
                  setErrors({ ...errors, dataNascimento: validateDate(date) || '' });
                }
              }}
              error={errors.dataNascimento}
            />
          </View>

          <View style={styles.buttons}>
            <TouchableOpacity style={styles.cancelButton} onPress={handleClose}>
              <Text style={styles.cancelButtonText}>Cancelar</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.addButton} onPress={handleSubmit}>
              <Text style={styles.addButtonText}>Adicionar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: '#fff',
    borderRadius: 16,
    width: '90%',
    maxWidth: 400,
    maxHeight: '80%',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e1e8ed',
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#2c3e50',
  },
  closeButton: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: '#e74c3c',
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  content: {
    padding: 20,
  },
  dateContainer: {
    marginBottom: 16,
  },
  label: {
    fontSize: 14,
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: 8,
  },
  buttons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  cancelButton: {
    flex: 1,
    backgroundColor: '#95a5a6',
    padding: 16,
    borderRadius: 12,
    marginRight: 10,
    alignItems: 'center',
  },
  cancelButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
  addButton: {
    flex: 1,
    backgroundColor: '#27ae60',
    padding: 16,
    borderRadius: 12,
    marginLeft: 10,
    alignItems: 'center',
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
});
