import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
} from 'react-native';
import ClientesList from '../components/ClientesList';
import EstatisticasCard from '../components/EstatisticasCard';
import AddClienteModal from '../components/AddClienteModal';
import { api } from '../services/api';

interface DashboardScreenProps {
  onLogout: () => void;
}

interface Cliente {
  id: number;
  nomeCompleto: string;
  email: string;
  dataNascimento: string;
  vendas: Array<{
    data: string;
    valor: number;
  }>;
}

interface Estatisticas {
  maiorVolume: {
    cliente: string;
    total: number;
  };
  maiorMedia: {
    cliente: string;
    media: number;
  };
  maiorFrequencia: {
    cliente: string;
    dias: number;
  };
}

export default function DashboardScreen({ onLogout }: DashboardScreenProps) {
  const [clientes, setClientes] = useState<Cliente[]>([]);
  const [estatisticas, setEstatisticas] = useState<Estatisticas | null>(null);
  const [vendasPorDia, setVendasPorDia] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setIsLoading(true);
      await Promise.all([
        loadClientes(),
        loadEstatisticas(),
        loadVendasPorDia(),
      ]);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loadClientes = async () => {
    try {
      const response = await api.get('/clientes');
      const normalizedClientes = normalizeClientesData(response.data);
      setClientes(normalizedClientes);
    } catch (error) {
      console.error('Error loading clientes:', error);
    }
  };

  const loadEstatisticas = async () => {
    try {
      const response = await api.get('/vendas/estatisticas');
      setEstatisticas(response.data);
    } catch (error) {
      console.error('Error loading estatisticas:', error);
    }
  };

  const loadVendasPorDia = async () => {
    try {
      const response = await api.get('/vendas/por-dia');
      setVendasPorDia(response.data);
    } catch (error) {
      console.error('Error loading vendas por dia:', error);
    }
  };

  const normalizeClientesData = (data: any): Cliente[] => {
    // O backend retorna um array simples de clientes
    if (Array.isArray(data)) {
      return data.map((cliente: any) => ({
        id: cliente.id,
        nomeCompleto: cliente.nomeCompleto,
        email: cliente.email,
        dataNascimento: cliente.dataNascimento,
        vendas: cliente.vendas || [],
      }));
    }
    
    // Fallback para estrutura aninhada (caso exista)
    if (data.data?.clientes) {
      return data.data.clientes.map((cliente: any) => ({
        id: cliente.info?.id || Math.random(),
        nomeCompleto: cliente.info?.nomeCompleto || '',
        email: cliente.info?.detalhes?.email || '',
        dataNascimento: cliente.info?.detalhes?.nascimento || '',
        vendas: cliente.estatisticas?.vendas || [],
      }));
    }
    
    return [];
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadData();
    setRefreshing(false);
  };

  const handleAddCliente = async (novoCliente: any) => {
    try {
      await api.post('/clientes', novoCliente);
      await loadData();
      setShowAddModal(false);
    } catch (error) {
      console.error('Error adding cliente:', error);
    }
  };

  const getFirstMissingLetter = (nome: string): string => {
    const alphabet = 'abcdefghijklmnopqrstuvwxyz';
    const nomeLower = nome.toLowerCase();
    
    for (let letter of alphabet) {
      if (!nomeLower.includes(letter)) {
        return letter.toUpperCase();
      }
    }
    return '-';
  };

  if (isLoading) {
    return (
      <View style={styles.container}>
        <Text style={styles.loadingText}>Carregando...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Loja de Brinquedos</Text>
        <TouchableOpacity style={styles.logoutButton} onPress={onLogout}>
          <Text style={styles.logoutText}>Sair</Text>
        </TouchableOpacity>
      </View>

      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        <View style={styles.content}>
          <TouchableOpacity
            style={styles.addButton}
            onPress={() => setShowAddModal(true)}
          >
            <Text style={styles.addButtonText}>+ Adicionar Cliente</Text>
          </TouchableOpacity>

          {estatisticas && (
            <EstatisticasCard estatisticas={estatisticas} />
          )}

          {vendasPorDia.length > 0 && (
            <View style={styles.chartContainer}>
              <Text style={styles.chartTitle}>Vendas por Dia</Text>
              <View style={styles.chartPlaceholder}>
                <Text style={styles.chartPlaceholderText}>
                  Gr√°fico de vendas por dia
                </Text>
                <Text style={styles.chartPlaceholderSubtext}>
                  {vendasPorDia.length} dias com vendas registradas
                </Text>
              </View>
            </View>
          )}

          <ClientesList
            clientes={clientes}
            getFirstMissingLetter={getFirstMissingLetter}
            onRefresh={loadClientes}
          />
        </View>
      </ScrollView>

      <AddClienteModal
        visible={showAddModal}
        onClose={() => setShowAddModal(false)}
        onAdd={handleAddCliente}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingTop: 50,
    paddingBottom: 20,
    backgroundColor: '#fff',
    borderBottomWidth: 1,
    borderBottomColor: '#e1e8ed',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#2c3e50',
  },
  logoutButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    backgroundColor: '#e74c3c',
    borderRadius: 8,
  },
  logoutText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: '600',
  },
  scrollView: {
    flex: 1,
  },
  content: {
    padding: 20,
  },
  addButton: {
    backgroundColor: '#27ae60',
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 20,
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
  chartContainer: {
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 12,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  chartTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: 16,
    textAlign: 'center',
  },
  chartPlaceholder: {
    height: 200,
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 2,
    borderColor: '#e1e8ed',
    borderStyle: 'dashed',
  },
  chartPlaceholderText: {
    fontSize: 16,
    color: '#7f8c8d',
    marginBottom: 8,
  },
  chartPlaceholderSubtext: {
    fontSize: 14,
    color: '#95a5a6',
  },
  loadingText: {
    fontSize: 18,
    textAlign: 'center',
    marginTop: 50,
    color: '#7f8c8d',
  },
});
