import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  FlatList,
  ActivityIndicator,
} from 'react-native';
import { api } from '../services/api';
import AddVendaModal from './AddVendaModal';
import CustomModal from './CustomModal';

interface Venda {
  id: number;
  valor: number;
  data: string;
}

interface VendasModalProps {
  visible: boolean;
  onClose: () => void;
  clienteId: number;
  clienteNome: string;
  onAddVenda: () => void;
}

export default function VendasModal({
  visible,
  onClose,
  clienteId,
  clienteNome,
  onAddVenda,
}: VendasModalProps) {
  const [vendas, setVendas] = useState<Venda[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showAddVendaModal, setShowAddVendaModal] = useState(false);

  useEffect(() => {
    if (visible && clienteId) {
      loadVendas();
    }
  }, [visible, clienteId]);

  const loadVendas = async () => {
    setIsLoading(true);
    try {
      const response = await api.get(`/vendas/cliente/${clienteId}`);
      setVendas(response.data);
    } catch (error) {
      console.error('Erro ao carregar vendas:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR');
  };

  const renderVenda = ({ item }: { item: Venda }) => (
    <View style={styles.vendaItem}>
      <View style={styles.vendaInfo}>
        <Text style={styles.vendaData}>{formatDate(item.data)}</Text>
        <Text style={styles.vendaValor}>{formatCurrency(item.valor)}</Text>
      </View>
    </View>
  );

  const totalVendas = vendas.reduce((sum, venda) => sum + Number(venda.valor), 0);

  return (
    <CustomModal visible={visible}>
      <View style={styles.modalContent}>
        <View style={styles.header}>
          <Text style={styles.title}>Vendas de {clienteNome}</Text>
          <View style={styles.headerActions}>
            <TouchableOpacity
              style={styles.addButton}
              onPress={() => setShowAddVendaModal(true)}
            >
              <Text style={styles.addButtonText}>+</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.closeButton} onPress={onClose}>
              <Text style={styles.closeButtonText}>✕</Text>
            </TouchableOpacity>
          </View>
        </View>

        {isLoading ? (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#3498db" />
            <Text style={styles.loadingText}>Carregando vendas...</Text>
          </View>
        ) : (
          <>
            <View style={styles.summary}>
              <Text style={styles.summaryText}>
                Total de vendas: {vendas.length}
              </Text>
              <Text style={styles.summaryText}>
                Valor total: {formatCurrency(totalVendas)}
              </Text>
              {vendas.length > 0 && (
                <Text style={styles.summaryText}>
                  Média por venda: {formatCurrency(totalVendas / vendas.length)}
                </Text>
              )}
            </View>

            {vendas.length === 0 ? (
              <View style={styles.emptyContainer}>
                <Text style={styles.emptyText}>Nenhuma venda registrada</Text>
              </View>
            ) : (
              <FlatList
                data={vendas}
                renderItem={renderVenda}
                keyExtractor={(item) => item.id.toString()}
                style={styles.vendasList}
                showsVerticalScrollIndicator={false}
              />
            )}
          </>
        )}
      </View>

      <AddVendaModal
        visible={showAddVendaModal}
        clienteId={clienteId}
        clienteNome={clienteNome}
        onSuccess={loadVendas}
      />
    </CustomModal>
  );
}

const styles = StyleSheet.create({
  modalContent: {
    flex: 1,
    padding: 20,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e1e8ed',
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#2c3e50',
    flex: 1,
  },
  headerActions: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  addButton: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: '#27ae60',
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 10,
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  closeButton: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: '#e74c3c',
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  loadingContainer: {
    padding: 40,
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#7f8c8d',
  },
  summary: {
    padding: 20,
    backgroundColor: '#f8f9fa',
    borderBottomWidth: 1,
    borderBottomColor: '#e1e8ed',
  },
  summaryText: {
    fontSize: 14,
    color: '#2c3e50',
    marginBottom: 4,
    fontWeight: '500',
  },
  emptyContainer: {
    padding: 40,
    alignItems: 'center',
  },
  emptyText: {
    fontSize: 16,
    color: '#7f8c8d',
    textAlign: 'center',
  },
  vendasList: {
    flex: 1,
  },
  vendaItem: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#f1f2f6',
  },
  vendaInfo: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  vendaData: {
    fontSize: 16,
    color: '#2c3e50',
    fontWeight: '500',
  },
  vendaValor: {
    fontSize: 16,
    color: '#27ae60',
    fontWeight: 'bold',
  },
});
