import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  Alert,
  TextInput,
} from 'react-native';
import { api } from '../services/api';
import VendasModal from './VendasModal';

interface Cliente {
  id: number;
  nomeCompleto: string;
  email: string;
  dataNascimento: string;
  vendas: Array<{
    data: string;
    valor: number;
  }>;
}

interface ClientesListProps {
  clientes: Cliente[];
  getFirstMissingLetter: (nome: string) => string;
  onRefresh: () => void;
}

export default function ClientesList({
  clientes,
  getFirstMissingLetter,
  onRefresh,
}: ClientesListProps) {
  const [filtro, setFiltro] = useState('');
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editForm, setEditForm] = useState({
    nomeCompleto: '',
    email: '',
    dataNascimento: '',
  });
  const [showVendasModal, setShowVendasModal] = useState(false);
  const [selectedCliente, setSelectedCliente] = useState<Cliente | null>(null);

  const filteredClientes = clientes.filter(
    (cliente) =>
      cliente.nomeCompleto.toLowerCase().includes(filtro.toLowerCase()) ||
      cliente.email.toLowerCase().includes(filtro.toLowerCase())
  );

  const handleEdit = (cliente: Cliente) => {
    setEditingId(cliente.id);
    setEditForm({
      nomeCompleto: cliente.nomeCompleto,
      email: cliente.email,
      dataNascimento: cliente.dataNascimento,
    });
  };

  const handleSave = async () => {
    if (!editingId) return;

    try {
      await api.patch(`/clientes/${editingId}`, editForm);
      setEditingId(null);
      onRefresh();
    } catch (error) {
      Alert.alert('Erro', 'Erro ao atualizar cliente');
    }
  };

  const handleDelete = (id: number) => {
    Alert.alert(
      'Confirmar exclusÃ£o',
      'Tem certeza que deseja excluir este cliente?',
      [
        { text: 'Cancelar', style: 'cancel' },
        {
          text: 'Excluir',
          style: 'destructive',
          onPress: async () => {
            try {
              await api.delete(`/clientes/${id}`);
              onRefresh();
            } catch (error) {
              Alert.alert('Erro', 'Erro ao excluir cliente');
            }
          },
        },
      ]
    );
  };

  const handleViewVendas = (cliente: Cliente) => {
    setSelectedCliente(cliente);
    setShowVendasModal(true);
  };

  const renderCliente = ({ item }: { item: Cliente }) => {
    const isEditing = editingId === item.id;
    const missingLetter = getFirstMissingLetter(item.nomeCompleto);

    return (
      <View style={styles.clienteCard}>
        <View style={styles.clienteHeader}>
          <View style={styles.clienteInfo}>
            {isEditing ? (
              <TextInput
                style={styles.editInput}
                value={editForm.nomeCompleto}
                onChangeText={(text) =>
                  setEditForm({ ...editForm, nomeCompleto: text })
                }
                placeholder="Nome completo"
              />
            ) : (
              <Text style={styles.clienteNome}>{item.nomeCompleto}</Text>
            )}
            <View style={styles.missingLetterContainer}>
              <Text style={styles.missingLetterLabel}>Letra faltante:</Text>
              <Text style={styles.missingLetter}>{missingLetter}</Text>
            </View>
          </View>
          <View style={styles.actions}>
            {isEditing ? (
              <>
                <TouchableOpacity
                  style={[styles.actionButton, styles.saveButton]}
                  onPress={handleSave}
                >
                  <Text style={styles.actionButtonText}>Salvar</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.actionButton, styles.cancelButton]}
                  onPress={() => setEditingId(null)}
                >
                  <Text style={styles.actionButtonText}>Cancelar</Text>
                </TouchableOpacity>
              </>
            ) : (
              <>
                <TouchableOpacity
                  style={[styles.actionButton, styles.viewButton]}
                  onPress={() => handleViewVendas(item)}
                >
                  <Text style={styles.actionButtonText}>Vendas</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.actionButton, styles.editButton]}
                  onPress={() => handleEdit(item)}
                >
                  <Text style={styles.actionButtonText}>Editar</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.actionButton, styles.deleteButton]}
                  onPress={() => handleDelete(item.id)}
                >
                  <Text style={styles.actionButtonText}>Excluir</Text>
                </TouchableOpacity>
              </>
            )}
          </View>
        </View>

        <View style={styles.clienteDetails}>
          {isEditing ? (
            <>
              <TextInput
                style={styles.editInput}
                value={editForm.email}
                onChangeText={(text) =>
                  setEditForm({ ...editForm, email: text })
                }
                placeholder="Email"
                keyboardType="email-address"
              />
              <TextInput
                style={styles.editInput}
                value={editForm.dataNascimento}
                onChangeText={(text) =>
                  setEditForm({ ...editForm, dataNascimento: text })
                }
                placeholder="Data de nascimento (YYYY-MM-DD)"
              />
            </>
          ) : (
            <>
              <Text style={styles.clienteDetail}>
                <Text style={styles.detailLabel}>Email:</Text> {item.email}
              </Text>
              <Text style={styles.clienteDetail}>
                <Text style={styles.detailLabel}>Nascimento:</Text>{' '}
                {new Date(item.dataNascimento).toLocaleDateString('pt-BR')}
              </Text>
              <Text style={styles.clienteDetail}>
                <Text style={styles.detailLabel}>Total de vendas:</Text>{' '}
                {item.vendas.length}
              </Text>
            </>
          )}
        </View>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>Clientes</Text>
      
      <TextInput
        style={styles.searchInput}
        placeholder="Filtrar por nome ou email..."
        value={filtro}
        onChangeText={setFiltro}
      />

      <FlatList
        data={filteredClientes}
        renderItem={renderCliente}
        keyExtractor={(item) => item.id.toString()}
        showsVerticalScrollIndicator={false}
        contentContainerStyle={styles.listContainer}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#2c3e50',
    marginBottom: 16,
  },
  searchInput: {
    backgroundColor: '#fff',
    borderRadius: 8,
    padding: 12,
    marginBottom: 16,
    borderWidth: 1,
    borderColor: '#e1e8ed',
  },
  listContainer: {
    paddingBottom: 20,
  },
  clienteCard: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  clienteHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    marginBottom: 12,
  },
  clienteInfo: {
    flex: 1,
  },
  clienteNome: {
    fontSize: 18,
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: 8,
  },
  missingLetterContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  missingLetterLabel: {
    fontSize: 12,
    color: '#7f8c8d',
    marginRight: 4,
  },
  missingLetter: {
    fontSize: 14,
    fontWeight: 'bold',
    color: '#e74c3c',
    backgroundColor: '#fdf2f2',
    paddingHorizontal: 6,
    paddingVertical: 2,
    borderRadius: 4,
  },
  actions: {
    flexDirection: 'row',
    gap: 8,
  },
  actionButton: {
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 6,
  },
  editButton: {
    backgroundColor: '#3498db',
  },
  deleteButton: {
    backgroundColor: '#e74c3c',
  },
  saveButton: {
    backgroundColor: '#27ae60',
  },
  cancelButton: {
    backgroundColor: '#95a5a6',
  },
  actionButtonText: {
    color: '#fff',
    fontSize: 12,
    fontWeight: '600',
  },
  clienteDetails: {
    gap: 4,
  },
  clienteDetail: {
    fontSize: 14,
    color: '#34495e',
  },
  detailLabel: {
    fontWeight: '600',
  },
  editInput: {
    backgroundColor: '#f8f9fa',
    borderRadius: 6,
    padding: 8,
    marginBottom: 8,
    borderWidth: 1,
    borderColor: '#e1e8ed',
  },
  viewButton: {
    backgroundColor: '#2ecc71',
  },
});
