import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Alert,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import CustomModal from './CustomModal';

interface AddClienteModalProps {
  visible: boolean;
  onClose: () => void;
  onAdd: (cliente: any) => void;
}

export default function AddClienteModal({
  visible,
  onClose,
  onAdd,
}: AddClienteModalProps) {
  const [form, setForm] = useState({
    nomeCompleto: '',
    email: '',
    dataNascimento: '',
  });

  const handleSubmit = () => {
    if (!form.nomeCompleto || !form.email || !form.dataNascimento) {
      Alert.alert('Erro', 'Por favor, preencha todos os campos');
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(form.email)) {
      Alert.alert('Erro', 'Por favor, insira um email v치lido');
      return;
    }

    const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
    if (!dateRegex.test(form.dataNascimento)) {
      Alert.alert('Erro', 'Por favor, insira uma data v치lida (YYYY-MM-DD)');
      return;
    }

    onAdd(form);
    setForm({ nomeCompleto: '', email: '', dataNascimento: '' });
  };

  const handleCancel = () => {
    setForm({ nomeCompleto: '', email: '', dataNascimento: '' });
    onClose();
  };

  return (
    <CustomModal visible={visible}>
      <View style={styles.modalContent}>
        <Text style={styles.modalTitle}>Adicionar Cliente</Text>

        <TextInput
          style={styles.input}
          placeholder="Nome completo"
          value={form.nomeCompleto}
          onChangeText={(text) => setForm({ ...form, nomeCompleto: text })}
        />

        <TextInput
          style={styles.input}
          placeholder="Email"
          value={form.email}
          onChangeText={(text) => setForm({ ...form, email: text })}
          keyboardType="email-address"
          autoCapitalize="none"
        />

        <TextInput
          style={styles.input}
          placeholder="Data de nascimento (YYYY-MM-DD)"
          value={form.dataNascimento}
          onChangeText={(text) => setForm({ ...form, dataNascimento: text })}
        />

        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={[styles.button, styles.cancelButton]}
            onPress={handleCancel}
          >
            <Text style={styles.cancelButtonText}>Cancelar</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={[styles.button, styles.addButton]}
            onPress={handleSubmit}
          >
            <Text style={styles.addButtonText}>Adicionar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </CustomModal>
  );
}

const styles = StyleSheet.create({
  modalContent: {
    padding: 24,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: 24,
  },
  input: {
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
    padding: 16,
    marginBottom: 16,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#e1e8ed',
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    gap: 12,
    marginTop: 8,
  },
  button: {
    flex: 1,
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
  },
  cancelButton: {
    backgroundColor: '#95a5a6',
  },
  addButton: {
    backgroundColor: '#27ae60',
  },
  cancelButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
});
