import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
} from 'react-native';
import ValidatedInput from './ValidatedInput';
import DatePicker from './DatePicker';
import CustomModal from './CustomModal';
import { validateValue, validateDate, formatDateForBackend } from '../utils/validation';
import { api } from '../services/api';

interface AddVendaModalProps {
  visible: boolean;
  clienteId: number;
  clienteNome: string;
  onSuccess: () => void;
}

export default function AddVendaModal({ visible, clienteId, clienteNome, onSuccess }: AddVendaModalProps) {
  const [form, setForm] = useState({
    valor: '',
    data: '',
  });

  const [errors, setErrors] = useState({
    valor: '',
    data: '',
  });

  // Estados para os modais
  const [errorModalVisible, setErrorModalVisible] = useState(false);
  const [successModalVisible, setSuccessModalVisible] = useState(false);
  const [apiErrorModalVisible, setApiErrorModalVisible] = useState(false);

  const validateForm = (): boolean => {
    const newErrors = {
      valor: validateValue(form.valor) || '',
      data: validateDate(form.data) || '',
    };

    setErrors(newErrors);

    return !Object.values(newErrors).some(error => error !== '');
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      setErrorModalVisible(true);
      return;
    }

    try {
      const vendaData = {
        clienteId,
        valor: parseFloat(form.valor),
        data: formatDateForBackend(form.data),
      };

      await api.post('/vendas', vendaData);
      setSuccessModalVisible(true);
      handleClose();
      onSuccess();
    } catch (error) {
      setApiErrorModalVisible(true);
    }
  };

  const handleClose = () => {
    setForm({
      valor: '',
      data: '',
    });
    setErrors({
      valor: '',
      data: '',
    });
  };

  if (!visible) return null;

  return (
    <>
      <View style={styles.overlay}>
        <View style={styles.modal}>
          <View style={styles.header}>
            <Text style={styles.title}>Adicionar Venda</Text>
            <TouchableOpacity style={styles.closeButton} onPress={handleClose}>
              <Text style={styles.closeButtonText}>✕</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.content}>
            <Text style={styles.clienteInfo}>Cliente: {clienteNome}</Text>

            <ValidatedInput
              label="Valor (R$)"
              placeholder="0,00"
              value={form.valor}
              onChangeText={(text) => {
                setForm({ ...form, valor: text });
                if (errors.valor) {
                  setErrors({ ...errors, valor: validateValue(text) || '' });
                }
              }}
              error={errors.valor}
              keyboardType="numeric"
            />

            <View style={styles.dateContainer}>
              <Text style={styles.label}>Data da Venda</Text>
              <DatePicker
                value={form.data}
                onChange={(date) => {
                  setForm({ ...form, data: date });
                  if (errors.data) {
                    setErrors({ ...errors, data: validateDate(date) || '' });
                  }
                }}
                error={errors.data}
              />
            </View>

            <View style={styles.buttons}>
              <TouchableOpacity style={styles.cancelButton} onPress={handleClose}>
                <Text style={styles.cancelButtonText}>Cancelar</Text>
              </TouchableOpacity>
              <TouchableOpacity style={styles.addButton} onPress={handleSubmit}>
                <Text style={styles.addButtonText}>Adicionar</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </View>

      <CustomModal
        visible={errorModalVisible}
        title="Erro"
        message="Por favor, corrija os erros no formulário"
        type="error"
        onConfirm={() => setErrorModalVisible(false)}
        confirmText="OK"
      />

      <CustomModal
        visible={successModalVisible}
        title="Sucesso"
        message="Venda adicionada com sucesso!"
        type="success"
        onConfirm={() => setSuccessModalVisible(false)}
        confirmText="OK"
      />

      <CustomModal
        visible={apiErrorModalVisible}
        title="Erro"
        message="Erro ao adicionar venda"
        type="error"
        onConfirm={() => setApiErrorModalVisible(false)}
        confirmText="OK"
      />
    </>
  );
}

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: '#fff',
    borderRadius: 16,
    width: '90%',
    maxWidth: 400,
    maxHeight: '80%',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e1e8ed',
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#2c3e50',
  },
  closeButton: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: '#e74c3c',
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  content: {
    padding: 20,
  },
  clienteInfo: {
    fontSize: 16,
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: 20,
    textAlign: 'center',
  },
  dateContainer: {
    marginBottom: 16,
  },
  label: {
    fontSize: 14,
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: 8,
  },
  buttons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  cancelButton: {
    flex: 1,
    backgroundColor: '#95a5a6',
    padding: 16,
    borderRadius: 12,
    marginRight: 10,
    alignItems: 'center',
  },
  cancelButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
  addButton: {
    flex: 1,
    backgroundColor: '#27ae60',
    padding: 16,
    borderRadius: 12,
    marginLeft: 10,
    alignItems: 'center',
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
});
